mapper.select:
SELECT u.USER_ID USER_ID, u.FULL_NAME FULL_NAME, u.BIRTHDAY BIRTHDAY, g.GROUP_NAME GROUP_NAME, u.EMAIL EMAIL, u.TEL TEL, j.NAME_LEVEL NAME_LEVEL, d.END_DATE END_DATE, d.TOTAL TOTAL, u.GROUP_ID GROUP_ID, j.CODE_LEVEL CODE_LEVEL FROM tbl_user u INNER JOIN MST_GROUP g ON g.group_id = u.group_id LEFT JOIN TBL_DETAIL_USER_JAPAN d ON d.user_id = u.user_id LEFT JOIN MST_JAPANSE j ON j.code_level = d.code_level
u.GROUP_ID = #{groupId}
AND Lower(FULL_NAME) LIKE Lower(#{fullName})
${order.property} ${order.direction}

mapper.update:
UPDATE TBL_USER
GROUP_ID = #{tblUser.groupId} , LOGIN_NAME = #{tblUser.loginName} , FULL_NAME = #{tblUser.fullName}, FULL_NAME_KANA = #{tblUser.fullNameKana} , EMAIL = #{tblUser.email} , TEL = #{tblUser.tel} , BIRTHDAY = #{tblUser.birthday}
WHERE USER_ID = #{tblUser.userId}
